# !/usr/bin/python# coding:utf-8# 脚本模板import syssys.path.append('./game')import g# 跳过模块内的where条件检测，若没有充分理由，请勿随意跳过g.m.pathDebug.SKIP_WHERE_CHECK = Trueclass Patch(object):    @g.m.pathDebug.patch    def syzc(self):        _flags = g.mdb.find("stagefund", {"key": "syzc"})        _nt = g.C.NOW()        _zt = g.C.ZERO(_nt)        _rtime = _zt + 30 * 24 * 3600        for f in _flags:            _id = f["_id"]            g.mdb.update("stagefund", {"_id": _id}, {"refreshtime": _rtime})    @g.m.pathDebug.run    def run(self):        # run名字固定，在这里指定执行顺序        self.syzc()if __name__ == '__main__':    patch = Patch()    patch.run()