# !/usr/bin/python# coding:utf-8# 脚本模板import syssys.path.append('./game')import g# 跳过模块内的where条件检测，若没有充分理由，请勿随意跳过g.m.pathDebug.SKIP_WHERE_CHECK = Trueclass Patch(object):    @g.m.pathDebug.patch    def christmas(self):        _title = "圣诞活动"        _content = "尊敬的勇士:我们对由于数据错误导致第二天圣诞袜未发送表示歉意。给您造成的不便还望谅解。以下是今天完成未能领取到的圣诞袜，请查收！感谢您的理解与支持，祝您圣诞游戏愉快！"        _prize = [{"a":"item", "t":"1188", "n":15}]        _backUid = []        _hdid = 1640166243        list = g.mdb.find("hddata", {"hdid":_hdid})        for i in list:            uid = i["uid"]            if not i["buysptask"]:                continue            if "2004" not in i["task"]["rec"]:                continue            if uid in _backUid:                continue            _backUid.append(uid)            g.m.emailfun.sendEmails([uid], 1, _title, _content, prize=_prize)    @g.m.pathDebug.run    def run(self):        # run名字固定，在这里指定执行顺序        self.christmas()if __name__ == '__main__':    patch = Patch()    patch.run()