# !/usr/bin/python# coding:utf-8# 脚本模板import syssys.path.append('./game')import g# 跳过模块内的where条件检测，若没有充分理由，请勿随意跳过g.m.pathDebug.SKIP_WHERE_CHECK = Trueclass Patch(object):    @g.m.pathDebug.patch    def resethero(self):        _heros = g.mdb.find("hero", {"hid": {"$in":["44066", "44065"]}})        for hero in _heros:            _setData = {}            if hero["hid"] in g.GC['herostarup'] and str(hero["star"]) in g.GC['herostarup'][hero["hid"]]:                _star_conf = g.GC['herostarup'][hero["hid"]]                con = _star_conf[str(hero["star"])]                _setData["bd1skill"] = con["bd1skill"]            else:                con = g.GC["hero"][hero["hid"]]                _setData["bd1skill"] = con["bd1skill"]            g.mdb.update("hero", {"_id": hero["_id"]}, _setData)            _hero_buff = g.m.herofun.reSetHeroBuff(hero["uid"], str(hero["_id"]), ['bdskillbuff'])    @g.m.pathDebug.run    def run(self):        # run名字固定，在这里指定执行顺序        self.resethero()if __name__ == '__main__':    patch = Patch()    patch.run()